package com.pfa.entities.users;

import com.pfa.dtos.SignupAdminRequestDTO;
import com.pfa.dtos.SignupDTO;
import com.pfa.entities.requests.RequestEntity;
import com.pfa.entities.users.enums.*;
import jakarta.persistence.*;
import lombok.*;
import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.crypto.password.PasswordEncoder;

import java.util.Arrays;
import java.util.Collection;
import java.util.Date;
import java.util.List;
import java.util.stream.Collectors;


@Builder
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
@Entity
@Table(name = "users")
@Inheritance(strategy = InheritanceType.JOINED)
public class UserEntity implements UserDetails {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Column(nullable = false)
    private String login;

    @Column(nullable = false)
    private String password;

    @Setter
    @Column(nullable = false, name = "client_active")
    private boolean active;

    @ElementCollection(targetClass = RoleEnum.class)
    @JoinTable(name = "user_roles", joinColumns = @JoinColumn(name = "role_id"))
    @Column(name = "role", nullable = false)
    @Enumerated(EnumType.STRING)
    Collection<RoleEnum> roles;

    @Column(name = "user_picture", length = Integer.MAX_VALUE)
    private String userPicture;

    @Column(name = "signature", length = Integer.MAX_VALUE)
    private String signature;

    @OneToMany(mappedBy = "userEntity")
    private List<RequestEntity> requestEntities;

    @Enumerated(EnumType.STRING)
    @Column(nullable = false)
    private IdentityTypeEnum identityType;

    @Column(name = "identity_code")
    private String identityCode;

    @Column
    private String firstname;

    @Column
    private String lastname;

    @Enumerated(EnumType.STRING)
    @Column(nullable = false)
    private SexeEnum sexe;

    @Column(name = "birth_day")
    private Date birthday;

    @Enumerated(EnumType.STRING)
    @Column(nullable = false)
    private NationaliteEnum nationality;

    @Enumerated(EnumType.STRING)
    @Column(nullable = false)
    private SituationFamilialeEnum familialSituationEnum;

    @Column
    private String email;

    @Column
    private String phoneNumber;

    @Column
    private String address;

    @Column
    private String fatherName;

    @Column
    private String motherName;

    @Column(name = "need_face_face")
    private boolean needFaceCheck;

    @Override
    public Collection<? extends GrantedAuthority> getAuthorities() {
        return this.roles
                .stream()
                .map(role -> (GrantedAuthority) role::name)
                .collect(Collectors.toList());
    }

    @Override
    public String getPassword() {
        return this.password;
    }

    @Override
    public String getUsername() {
        return this.login;
    }

    @Override
    public boolean isAccountNonExpired() {
        return true;
    }

    @Override
    public boolean isAccountNonLocked() {
        return true;
    }

    @Override
    public boolean isCredentialsNonExpired() {
        return false;
    }

    @Override
    public boolean isEnabled() {
        return this.active;
    }


    public static UserEntity buildAdministrator(PasswordEncoder passwordEncoder) {
        return UserEntity.builder()
                .id(null)
                .login("admin")
                .password(passwordEncoder.encode("admin"))
                .active(Boolean.TRUE)
                .roles(Arrays.asList(RoleEnum.ROLE_USER, RoleEnum.ROLE_ADMIN))
                .identityType(IdentityTypeEnum.CIN)
                .identityCode("admin")
                .userPicture("data:image/png;base64,")
                .familialSituationEnum(SituationFamilialeEnum.MARIE)
                .nationality(NationaliteEnum.MAROCAINE)
                .needFaceCheck(Boolean.FALSE)
                .sexe(SexeEnum.HOMME)
                .email("admin@admin.fr")
                .firstname("admin-firstname")
                .lastname("admin-lastname")
                .phoneNumber("5566778899")
                .build();
    }

    public static UserEntity buildSuperAdministrator(PasswordEncoder passwordEncoder) {
        return UserEntity.builder()
                .id(null)
                .login("superadmin")
                .password(passwordEncoder.encode("superadmin"))
                .active(Boolean.TRUE)
                .roles(Arrays.asList(RoleEnum.ROLE_USER, RoleEnum.ROLE_ADMIN, RoleEnum.ROLE_SUPER_ADMIN))
                .identityType(IdentityTypeEnum.CIN)
                .identityCode("superadmin")
                .userPicture("data:image/png;base64,")
                .familialSituationEnum(SituationFamilialeEnum.MARIE)
                .nationality(NationaliteEnum.MAROCAINE)
                .needFaceCheck(Boolean.FALSE)
                .email("superadmin@superadmin.fr")
                .firstname("superadmin-firstname")
                .lastname("superadmin-lastname")
                .phoneNumber("0011223344")
                .sexe(SexeEnum.HOMME)
                .build();
    }

    public static UserEntity from(SignupDTO signupDTO, PasswordEncoder passwordEncoder) {
        return UserEntity.builder()
                .id(null)
                .login(signupDTO.getIdentityCode())
                .password(passwordEncoder.encode(signupDTO.getPassword()))
                .identityType(IdentityTypeEnum.valueOf(signupDTO.getIdentityType()))
                .identityCode(signupDTO.getIdentityCode())
                .firstname(signupDTO.getFirstname())
                .lastname(signupDTO.getLastname())
                .sexe(SexeEnum.valueOf(signupDTO.getSexe()))
                .birthday(signupDTO.getBirthday())
                .nationality(NationaliteEnum.valueOf(signupDTO.getNationality()))
                .familialSituationEnum(SituationFamilialeEnum.valueOf(signupDTO.getFamilySituation()))
                .email(signupDTO.getEmail())
                .phoneNumber(signupDTO.getPhoneNumber())
                .address(signupDTO.getAddress())
                .fatherName(signupDTO.getFatherName())
                .motherName(signupDTO.getMotherName())
                .active(Boolean.FALSE)
                .roles(Arrays.asList(RoleEnum.ROLE_USER))
                .signature(signupDTO.getSignature())
                .userPicture(signupDTO.getUserPicture())
                .needFaceCheck(Boolean.FALSE)
                .build();
    }

    public static UserEntity from(SignupAdminRequestDTO signupAdminRequestDTO, PasswordEncoder passwordEncoder, boolean needFaceCheck, RoleEnum ... roleEnums) {
        return UserEntity.builder()
                .id(null)
                .login(signupAdminRequestDTO.getIdentityCode())
                .password(passwordEncoder.encode(signupAdminRequestDTO.getPassword()))
                .identityType(IdentityTypeEnum.valueOf(signupAdminRequestDTO.getIdentityType()))
                .identityCode(signupAdminRequestDTO.getIdentityCode())
                .firstname(signupAdminRequestDTO.getFirstname())
                .lastname(signupAdminRequestDTO.getLastname())
                .sexe(SexeEnum.valueOf(signupAdminRequestDTO.getSexe()))
                .birthday(signupAdminRequestDTO.getBirthday())
                .nationality(NationaliteEnum.valueOf(signupAdminRequestDTO.getNationality()))
                .familialSituationEnum(SituationFamilialeEnum.valueOf(signupAdminRequestDTO.getFamilySituation()))
                .email(signupAdminRequestDTO.getEmail())
                .phoneNumber(signupAdminRequestDTO.getPhoneNumber())
                .address(signupAdminRequestDTO.getAddress())
                .fatherName(signupAdminRequestDTO.getFatherName())
                .motherName(signupAdminRequestDTO.getMotherName())
                .active(Boolean.FALSE)
                .roles(Arrays.asList(roleEnums))
                .needFaceCheck(needFaceCheck)
                .build();
    }
}


